import React, { useState } from 'react';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [activeTab, setActiveTab] = useState('All');
  const [newTask, setNewTask] = useState('');

  // Filter tasks based on active tab
  const getFilteredTasks = () => {
    switch (activeTab) {
      case 'Active':
        return tasks.filter(task => task.active);
      case 'Completed':
        return tasks.filter(task => !task.active);
      default:
        return tasks;
    }
  };

  // Add new task
  const addTask = (e) => {
    e.preventDefault();
    if (newTask.trim() !== '') {
      const task = {
        id: Date.now(),
        text: newTask.trim(),
        active: true
      };
      setTasks([...tasks, task]);
      setNewTask('');
    }
  };

  // Toggle task status
  const toggleTask = (id) => {
    setTasks(tasks.map(task => 
      task.id === id ? { ...task, active: !task.active } : task
    ));
  };

  // Delete individual task
  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  // Delete all completed tasks
  const deleteAllCompleted = () => {
    setTasks(tasks.filter(task => task.active));
  };

  const filteredTasks = getFilteredTasks();
  const canAddTask = activeTab === 'All' || activeTab === 'Active';
  const canDelete = activeTab === 'Completed';

  return (
    <div className="todo-app">
      <div className="todo-container">
        <h1 className="todo-title">#todo</h1>
        
        {/* Tab Navigation */}
        <div className="tab-navigation">
          <button 
            className={`tab ${activeTab === 'All' ? 'active' : ''}`}
            onClick={() => setActiveTab('All')}
          >
            All
          </button>
          <button 
            className={`tab ${activeTab === 'Active' ? 'active' : ''}`}
            onClick={() => setActiveTab('Active')}
          >
            Active
          </button>
          <button 
            className={`tab ${activeTab === 'Completed' ? 'active' : ''}`}
            onClick={() => setActiveTab('Completed')}
          >
            Completed
          </button>
        </div>

        {/* Add Task Form - Only show in All and Active tabs */}
        {canAddTask && (
          <form className="add-task-form" onSubmit={addTask}>
            <input
              type="text"
              value={newTask}
              onChange={(e) => setNewTask(e.target.value)}
              placeholder="add details"
              className="task-input"
            />
            <button type="submit" className="add-button">
              Add
            </button>
          </form>
        )}

        {/* Task List */}
        <div className="task-list">
          {filteredTasks.length === 0 ? (
            <div className="empty-state">
              {activeTab === 'All' && 'No tasks yet. Add one above!'}
              {activeTab === 'Active' && 'No active tasks.'}
              {activeTab === 'Completed' && 'No completed tasks.'}
            </div>
          ) : (
            filteredTasks.map(task => (
              <div key={task.id} className={`task-item ${!task.active ? 'completed' : ''}`}>
                <div className="task-content">
                  <input
                    type="checkbox"
                    checked={!task.active}
                    onChange={() => toggleTask(task.id)}
                    className="task-checkbox"
                  />
                  <span className="task-text">{task.text}</span>
                </div>
                {canDelete && (
                  <button 
                    onClick={() => deleteTask(task.id)}
                    className="delete-button"
                    title="Delete task"
                  >
                    √ó
                  </button>
                )}
              </div>
            ))
          )}
        </div>

        {/* Delete All Completed Button - Only show in Completed tab */}
        {canDelete && filteredTasks.length > 0 && (
          <div className="delete-all-container">
            <button 
              onClick={deleteAllCompleted}
              className="delete-all-button"
            >
              üóëÔ∏è delete all
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
